SELECT /* PurchaseSacCommonMapper.searchPurchaseTenantPolicyList, 구매Part 테넌트 정책 목록 조회, 이승택/nTels, 2014-01-22 */ tenant_id , policy_id , policy_seq , policy_nm , proc_pattern_cd , tenant_prod_grp_cd , apply_unit_cd , apply_value , cond_unit_cd , cond_value , cond_period_unit_cd , cond_period_value , cond_clsf_unit_cd , cond_clsf_value , policy_prior FROM tb_cm_tenant_sale_policy WHERE tenant_id = #{tenantId}
AND proc_pattern_cd = #{procPatternCd}
AND (tenant_prod_grp_cd IS NULL OR #{tenantProdGrpCd, jdbcType=VARCHAR} LIKE CONCAT(tenant_prod_grp_cd, '%'))
AND SYSDATE BETWEEN start_dt AND expr_dt ORDER BY policy_id, policy_prior, policy_seq
