mapper.update:
INSERT /* DeviceSetMapper.createDevicePin, PIN 번호 등록, SAC(SC), 최진호, 2015-11-11 */ 
    INTO TB_US_USERMBR_DEVICE_SET
    (               
        TENANT_ID, 
        INSD_USERMBR_NO, 
        INSD_DEVICE_ID,
        REG_DT
      ,REG_ID 
      ,SYSTEM_ID 
       ,PIN_NO
       ,AUTH_CNT
       ,AUTH_LOCK_YN 
       ,AUTO_UPDT_YN 
       ,AUTO_UPDT_SET_CLSF    
       ,WI_FI_AUTO_UPDT_YN 
       ,LOGIN_LOCK_YN 
       ,APPR_PIN_REINS_YN 
       ,ADULT_CONTENTS_LOCK_YN 
       ,ADULT_CONTENTS_LIMT_YN 
       ,WI_FI_AUTO_DWLD_YN 
       ,ICAS_AUTH_YN 
       ,REAL_NAME_DATE 
       ,REAL_NAME_MDN 
    ) VALUES (              
        #{tenantID},
        #{userKey},
        #{deviceKey},
        SYSDATE
      ,#{userID} 
      ,#{systemID} 
       ,#{pinNo} 
       ,#{authCnt} 
       ,#{authLockYn} 
       ,#{isAutoUpdate} 
       ,#{autoUpdateSet}    
       ,#{isAutoUpdateWifi} 
       ,#{isLoginLock} 
       ,#{isPinRetry} 
       ,#{isAdult} 
       ,#{isAdultLock} 
       ,#{isDownloadWifiOnly} 
       ,#{isIcasAuth} 
       ,TO_DATE(#{realNameDate}, 'YYYYMMDDHH24MISS') 
       ,#{realNameMdn} 
    )

UPDATE /* DeviceSetMapper.modifyDeviceSet, 휴대기기 설정정보 등록/수정, SAC(SC), 최진호, 2015-11-11 */
    TB_US_USERMBR_DEVICE_SET
        SET
         UPD_DT = SYSDATE
       ,UPD_ID = #{userID} 
       ,PIN_NO = #{pinNo} 
       ,AUTH_CNT = #{authCnt} 
       ,AUTH_LOCK_YN = #{authLockYn} 
       ,AUTO_UPDT_YN = #{isAutoUpdate} 
       ,AUTO_UPDT_SET_CLSF = #{autoUpdateSet}    
       ,WI_FI_AUTO_UPDT_YN = #{isAutoUpdateWifi} 
       ,LOGIN_LOCK_YN = #{isLoginLock} 
       ,APPR_PIN_REINS_YN = #{isPinRetry} 
       ,ADULT_CONTENTS_LOCK_YN = #{isAdult} 
       ,ADULT_CONTENTS_LIMT_YN = #{isAdultLock} 
       ,WI_FI_AUTO_DWLD_YN = #{isDownloadWifiOnly} 
       ,ICAS_AUTH_YN = #{isIcasAuth}
       ,REAL_NAME_DATE = TO_DATE(#{realNameDate}, 'YYYYMMDDHH24MISS') 
       ,REAL_NAME_MDN = #{realNameMdn} 
WHERE tenant_id = #{tenantID}
  AND insd_usermbr_no = #{userKey}
  AND insd_device_id = #{deviceKey}

mapper.delete:
DELETE  /* DeviceSetMapper.removeDeviceSetInfo, 휴대기기 설정 정보 삭제, burkhan_rejoice, 2014-11-19 */
  FROM  TB_US_USERMBR_DEVICE_SET
 WHERE  tenant_id = #{tenantID}
   AND  insd_usermbr_no = #{userKey}
   AND  insd_device_id = #{deviceKey}
