mapper.update:
MERGE  /* UserMapper.xml, resetPasswordUser,  SAC(SC), 최진호, 2015-12-24 */
INTO tb_us_ousermbr_pwd m
USING  DUAL ON
    (
        m.insd_usermbr_no  = #{memberKey}
    )
/* 비밀번호가 존재하는 경우 기존 비밀번호 초기화 */
WHEN  MATCHED THEN
    UPDATE
        SET
            m.pwd = #{memberPW}
            ,m.act_tk = null
            ,m.upd_dt = SYSDATE
/* 비밀번호가 없는 경우 비밀번호 추가 */
WHEN  NOT MATCHED THEN
    INSERT
        (
            m.insd_usermbr_no
            ,m.pwd
            ,m.upd_dt
        )
    VALUES
        (
            #{memberKey}
            ,#{memberPW}
            ,SYSDATE
        )

MERGE  /* UserMapper.xml, updatePasswordUser, SAC(SC), 최진호, 2015-12-22 */
INTO tb_us_ousermbr_pwd u
USING DUAL ON
    (
        u.insd_usermbr_no  = #{memberKey}
    )
/* 비밀번호가 존재하는 경우 신규비밀번호로 업데이트 및 토큰삭제 */
WHEN MATCHED THEN
    UPDATE
        SET
            u.pwd = #{memberPW}
            ,u.act_tk = null
            ,u.upd_dt = TO_DATE(#{pwRegDate},'YYYYMMDDHH24MISS')
/* 비밀번호가 없는 경우 비밀번호 추가 */
WHEN NOT MATCHED THEN
    INSERT
        (
            u.insd_usermbr_no
            ,u.pwd
            ,u.upd_dt
        )
    VALUES
        (
            #{memberKey}
            ,#{memberPW}
            ,SYSDATE
        )

UPDATE /* UserMapper.xml, modifyUserPassword, SAC(SC), 최진호, 2016-01-14 */
    TB_US_OUSERMBR_PWD
SET
    PWD = #{newPassword}
    ,ACT_TK = null
    ,UPD_DT = SYSDATE
WHERE
    INSD_USERMBR_NO = #{userKey}
    AND PWD = #{oldPassword}
    AND IDP_ONEID_CD = #{userPwType}

mapper.select:
SELECT /* UserMapper.xml, searchUserD, SAC(SC), 최진호, 2015-12-21 */ DISTINCT(l.userKey) as userKey, ( SELECT TO_CHAR(upd_dt ,'YYYYMMDDHH24MISS') FROM tb_us_ousermbr_pwd WHERE insd_usermbr_no = l.userKey ) as pwRegDate, ( SELECT COUNT(1) FROM tb_us_ousermbr_device c WHERE c.insd_usermbr_no = l.userKey ) AS totalDeviceCount, #{isDormant} AS isDormant FROM ( SELECT a.insd_usermbr_no as userKey FROM tb_us_ousermbr a LEFT JOIN tb_us_ousermbr_device b ON a.insd_usermbr_no = b.insd_usermbr_no WHERE a.mbr_status_main_cd != 'US010202' /* 메인상태 != 탈퇴 US010202 */ AND b.auth_yn = 'Y'
b.insd_device_id = #{keySearch.keyString} b.device_id = #{keySearch.keyString} b.mdn = #{keySearch.keyString}
ORDER BY b.auth_dt DESC) l WHERE ROWNUM = 1

SELECT  /* UserMapper.xml, getUserKeyByKeySearchList, SAC(SC), 최진호, 2015-12-21 */
        DISTINCT(a.insd_usermbr_no) as userKey
  FROM  tb_us_ousermbr a
 WHERE  a.mbr_status_main_cd != 'US010202' /* 메인상태 != 탈퇴 US010202 */

    a.insd_usermbr_no =  #{keySearch.keyString} 
    a.mbr_id =  #{keySearch.keyString} 
    a.email_addr =  #{keySearch.keyString}

   SELECT  /* UserMapper.xml, getUserKeyByKeySearchListD, SAC(SC), 최진호, 2015-12-21 */
           DISTINCT(u.insd_usermbr_no) as userKey
     FROM  ( SELECT a.insd_usermbr_no FROM tb_us_ousermbr a
LEFT JOIN  tb_us_ousermbr_device b ON a.insd_usermbr_no = b.insd_usermbr_no
    WHERE  a.mbr_status_main_cd != 'US010202' /* 메인상태 != 탈퇴 US010202 */
      AND  b.auth_yn = 'Y'

       b.insd_device_id =  #{keySearch.keyString} 
       b.device_id =  #{keySearch.keyString} 
       b.mdn =  #{keySearch.keyString} 
       b.svc_mang_no = #{keySearch.keyString}

    ORDER BY b.auth_dt DESC) u
    WHERE ROWNUM = 1

