,#{permissionType} ,#{isUsed} ,#{limitAmount} ,#{preLimitAmount} ,#{lineMangStatus} ,to_date(#{endDate}, 'YYYYMMDDHH24MISS') ,to_date(#{insDate}, 'YYYYMMDDHH24MISS') )

mapper.update:
 MERGE  /* UserMapper.updatePolicy, policy 등록/수정, wisestone_dinga, 2013-12-30 */
        INTO tb_us_olimt_target u
 USING  DUAL ON
        (
                u.limt_policy_cd  = #{limitPolicyCode} 
            AND u.limt_policy_key  = #{limitPolicyKey}
        )
  WHEN  MATCHED THEN
UPDATE  SET 
        u.upd_dt = SYSDATE
        , u.start_dt = SYSDATE
        , u.end_dt = TO_DATE('29991231235959', 'YYYYMMDDHH24MISS')
        ,u.policy_apply_value  = #{policyApplyValue} 
        ,u.upd_id = #{updateID} 

        ,u.pmt_type = #{permissionType} 
        ,u.use_yn = #{isUsed} 


        ,u.pre_limt_amt = (SELECT  limt_amt 
                            FROM  tb_us_olimt_target
                           WHERE  limt_policy_cd = #{limitPolicyCode}
                             AND  limt_policy_key = #{limitPolicyKey}
                           )
        ,u.limt_amt = #{limitAmount}

  WHEN  NOT MATCHED THEN
INSERT  (
            seq,
            limt_policy_cd,
            start_dt,
            reg_id,
            reg_dt
        ,policy_apply_value 
        ,upd_id 
        ,limt_policy_key 

        ,pmt_type 
        ,use_yn 
        ,limt_amt 
        ,pre_limt_amt 
        ) 
VALUES  (
            SQ_US_LIMT_TARGET.NEXTVAL,
            #{limitPolicyCode},
            SYSDATE,
            #{regID},
            SYSDATE
        ,#{policyApplyValue} 
        ,#{updateID} 
        ,#{limitPolicyKey} 

        ,#{permissionType} 
        ,#{isUsed} 
        ,#{limitAmount} 
        ,#{preLimitAmount} 
        )

