mapper.select:
SELECT /* UserMapper.retrievePolicyList, 제한정책, wisestone_dinga, 2014-01-21 */ seq limitTargetNo , limt_policy_key limitPolicyKey , limt_policy_cd limitPolicyCode , policy_apply_value policyApplyValue , TO_CHAR( start_dt,'YYYYMMDDHH24MISS') startDate , TO_CHAR( end_dt,'YYYYMMDDHH24MISS') endDate , TO_CHAR( reg_dt,'YYYYMMDDHH24MISS') regDate , reg_id regID , upd_id updateID
, TO_CHAR( upd_dt,'YYYYMMDDHH24MISS') updateDate , pmt_type permissionType , use_yn isUsed , limt_amt limitAmount , pre_limt_amt preLimitAmount , ins_dt insDate , line_mang_status lineMangStatus FROM tb_us_olimt_target WHERE end_dt > SYSDATE AND limt_policy_key = #{limitPolicyKey} AND
limt_policy_cd = #{keySearch}

SELECT  /* UserMapper.searchChangedDevice, 휴대기기 기기변경이력 정보 조회, wisestone_dinga, 2014-02-17 */
       DISTINCT (insd_device_id) AS deviceKey
  FROM  tb_us_ousermbr_device_his 
 WHERE  chg_case_cd = 'US012002'
   AND  insd_usermbr_no = #{userKey}
   AND  auth_yn = 'Y'
   AND  upd_dt > (SYSDATE-30)
 AND  device_id = #{deviceID}
 AND  insd_device_id = #{deviceKey}

SELECT /* UserMapper.searchIDPChangedDevice2, 휴대기기 기기변경이력 정보 조회, 황정택/와이즈스톤, 2014-02-17 */
       insd_device_id AS deviceKey
 FROM  tb_us_ousermbr_device
WHERE  auth_yn = 'Y'
  AND  insd_usermbr_no = #{userKey}
 AND  device_id = #{deviceID}
  AND  insd_device_id = #{deviceKey}

SELECT  /* UserMapper.searchIDPChangedDevice, 휴대기기 기기변경이력 정보 조회, wisestone_dinga, 2014-02-17 */
DISTINCT (b.insd_device_id) AS deviceKey
FROM  tb_us_idp_log a, tb_us_ousermbr_device b
WHERE  a.chg_cd = 'US012002'
AND  a.insd_mbr_no = #{userKey}
AND  a.device_id = b.device_id
AND  a.insd_mbr_no = b.insd_usermbr_no
AND  a.reg_dt > (SYSDATE-30)
 AND  a.device_id = #{deviceID}
 AND  b.insd_device_id = #{deviceKey}

