mapper.select:
       /* CouponMapper.xml, getOneBrandSpecialProdCnt, One Store 에피소드id를 이용해 특가 여부 조회, 김형식/SK플래닛  , 2015-07-18 */
		SELECT 
		      COUNT(1) CNT
		 FROM TB_DP_SPRC_PROD
		WHERE PROD_ID =#{episodeId}
		  AND EXPO_YN = 'Y' 
		  AND CPN_ID IS NOT NULL

            #{item}

		  AND SYSDATE BETWEEN SPRC_APPLY_START_DT AND SPRC_APPLY_END_DT

