mapper.update:
/* CouponMapper.xml, updateDPCouponStatus, 전시단 쿠폰상품 상태변경, 김형식/SK플래닛  , 2014-01-08 */
UPDATE 
    TB_DP_TENANT_PROD
SET PROD_STATUS_CD = #{dpStatusCode}
   ,UPD_DT         = sysdate  
WHERE 1=1
  AND TENANT_ID =#{tenentId}

  AND PROD_ID =#{prodId}


  AND PROD_ID =#{itemCode}


  AND PROD_ID IN (
       SELECT 
              PROD_ID 
         FROM TB_DP_SHPG_PROD
        WHERE  PROD_ID = #{prodId}
    UNION ALL 
       SELECT 
               B.PART_PROD_ID
        FROM   TB_DP_SHPG_PROD A 
              ,TB_DP_PROD_RSHP B
        WHERE A.PROD_ID = B.PROD_ID
          AND B.PROD_RSHP_CD ='DP010802'                  
          AND A.PROD_ID = #{prodId}     
  )

mapper.select:
/* CouponMapper.xml, GET_COUPON_INFO, 특가 상품 상세 조회, 김형식/SK플래닛  , 2014-01-08 */
 SELECT 
     COUNT(PROD_ID) as CNT
 FROM TB_DP_SHPG_PROD
 WHERE SRC_CONTENT_ID =  #{itemCode}

/* CouponMapper.xml, SELECT_COUPON_VALID_STATUS,쿠폰 판매상태 여부 상태값, 김형식/SK플래닛  , 2014-01-08 */
 SELECT 
        PROD_STATUS_CD 
 FROM   TB_DP_TENANT_PROD 
 WHERE 1=1
 AND TENANT_ID ='S01'

   AND PROD_ID =#{newCouponId}


   AND PROD_ID =#{newItemId}

   AND EXPO_YN = 'Y'

mapper.update:
/* CouponMapper.xml, updateTbDpProdLastDeployDt, tb_dp_prod 최초 판매시간, 김형식/SK플래닛 , 2014-05-22 */ UPDATE TB_DP_PROD SET LAST_DEPLOY_DT = sysdate WHERE 1=1
AND PROD_ID =#{prodId}
AND PROD_ID =#{itemCode}
AND PROD_ID IN ( SELECT PROD_ID FROM TB_DP_SHPG_PROD WHERE PROD_ID = #{prodId} UNION ALL SELECT B.PART_PROD_ID FROM TB_DP_SHPG_PROD A ,TB_DP_PROD_RSHP B WHERE A.PROD_ID = B.PROD_ID AND B.PROD_RSHP_CD ='DP010802' AND A.PROD_ID = #{prodId} )
AND LAST_DEPLOY_DT IS NULL

mapper.select:
  /* CouponMapper.xml, getOneBrandSpecialProductCouponId, One Store 팅/특가 쿠폰 ID 조회, 김형식/SK플래닛  , 2015-07-18 */
   SELECT 
         CPN_ID
    FROM TB_DP_SPRC_PROD
   WHERE PROD_ID =#{episodeId}
     AND EXPO_YN = 'Y' 
     AND CPN_ID IS NOT NULL

       #{item}

     AND SYSDATE BETWEEN SPRC_APPLY_START_DT AND SPRC_APPLY_END_DT
ORDER BY CPN_ID
