mapper.select:
SELECT /* ArtistProductMapper.xml, selectArtistProductList, SAC 전시 , 2014-03-08 */ A.TOTAL_COUNT , A.RNK , A.TOP_MENU_ID , A.MENU_ID , A.PROD_ID AS PART_PROD_ID , A.CONTENTS_TYPE_CD FROM ( SELECT COUNT(A.PROD_ID) OVER() TOTAL_COUNT
, ROW_NUMBER() OVER(ORDER BY I.ISSUE_DAY DESC, D.PROD_NM) RNK
, ROW_NUMBER() OVER(ORDER BY NVL(SUM(H.PRCHS_CNT), 0) DESC, MAX(I.ISSUE_DAY) DESC, MAX(D.PROD_NM)) RNK
, C.TOP_MENU_ID , C.MENU_ID , A.PROD_ID , A.CONTENTS_TYPE_CD FROM TB_DP_PROD A /* 에피소드 */ JOIN TB_DP_MENU_CATEGORY_PROD_MAPG B ON B.PROD_ID = A.PROD_ID AND B.USE_YN = 'Y' JOIN TB_DP_MENU_CATEGORY C ON C.MENU_ID = B.MENU_ID AND C.USE_YN = 'Y' AND C.TOP_MENU_ID = 'DP16' JOIN TB_DP_PROD_DESC D ON D.PROD_ID = A.PROD_ID AND D.ARTIST1_ID = #{artistId} AND D.LANG_CD = #{langCd} JOIN TB_DP_TENANT_PROD E ON E.PROD_ID = A.PROD_ID AND E.TENANT_ID = #{tenantId} AND E.PROD_STATUS_CD = 'PD000403' AND E.EXPO_YN = 'Y' JOIN TB_DP_SPRT_DEVICE F ON F.PROD_ID = A.PROD_ID AND (F.DEVICE_MODEL_CD = #{deviceModelCd} OR F.DEVICE_MODEL_CD = #{mmDeviceModelCd}) JOIN TB_DP_MUSIC_PROD I ON I.PROD_ID = A.PROD_ID JOIN TB_CM_DEVICE G ON G.USE_YN = 'Y' AND G.MUSIC_SPRT_YN = 'Y' AND G.DEVICE_MODEL_CD = #{deviceModelCd}
LEFT JOIN TB_DP_TENANT_PROD_STATS H ON H.TENANT_ID IN #{v} AND H.PROD_ID = A.PROD_ID
WHERE 1=1 AND A.CONTENTS_TYPE_CD = 'PD002502' AND A.META_CLSF_CD = 'CT25'
AND A.PROD_GRD_CD IN
#{prodGradeCd}
GROUP BY A.PROD_ID, C.TOP_MENU_ID, C.MENU_ID, A.CONTENTS_TYPE_CD
) A WHERE A.RNK BETWEEN #{offset} AND #{count} + #{offset} - 1 ORDER BY A.RNK
