mapper.update:
MERGE INTO TB_DP_SOCIAL_STATS
USING DUAL
ON ( 1=1
	AND TENANT_ID = #{tenantId, jdbcType=VARCHAR}
	AND STATS_CLSF = #{clsf, jdbcType=VARCHAR}
	AND STATS_KEY = #{key, jdbcType=VARCHAR}
)
WHEN MATCHED THEN
	UPDATE SET
	CNT_LIKE = (CASE WHEN CNT_LIKE <= 0 THEN 0 ELSE CNT_LIKE - 1 END)
	CNT_SHAR = (CASE WHEN CNT_SHAR <= 0 THEN 0 ELSE CNT_SHAR - 1 END)
	CNT_BRWS = (CASE WHEN CNT_BRWS <= 0 THEN 0 ELSE CNT_BRWS - 1 END)
	, UPD_ID = 'SAC'
	, UPD_DT = SYSDATE
WHEN NOT MATCHED THEN
	INSERT (TENANT_ID, STATS_CLSF, STATS_KEY, CNT_LIKE, CNT_SHAR, CNT_BRWS, REG_DT, REG_ID, UPD_DT, UPD_ID)
	VALUES (
		#{tenantId, jdbcType=VARCHAR}
		, #{clsf, jdbcType=VARCHAR}
		, #{key, jdbcType=VARCHAR}
		, 0, 0, 0
		, SYSDATE
		, 'SAC'
		, SYSDATE
		, 'SAC'
	)
