mapper.select:
SELECT /* SearchKeywordMapper.xml, getSearchKeyword, SAC-전시기타/심대진/다모아솔루션, 2015-09-04 */ OPERATION_DT , SEARCH_NM , SEARCH_DESC , SEARCH_CLSF_CD , ROWNUM AS RANK , RANK_VARIATION , RANK_VARIATION_CD , KEYWORD FROM ( SELECT TO_CHAR(B.OPERATION_DT, 'YYYYMMDDHH24MISS') AS OPERATION_DT , A.SEARCH_NM , A.SEARCH_DESC , A.SEARCH_CLSF_CD , B.RANK , B.RANK_VARIATION , B.RANK_VARIATION_CD , B.KEYWORD , B.UPD_DT FROM TB_DP_SEARCH A, TB_DP_SEARCH_KEYWORD B WHERE A.TENANT_ID = B.TENANT_ID AND A.SEARCH_ID = B.SEARCH_ID AND A.STD_DT = B.STD_DT AND A.TENANT_ID = #{tenantId}
AND B.SEARCH_ID = #{searchId}
AND A.SEARCH_CLSF_CD = #{searchClsfCd}
AND A.EXPO_YN = 'Y' AND B.EXPO_YN = 'Y' ORDER BY B.RANK ASC, B.UPD_DT DESC ) WHERE ROWNUM <= #{count}

SELECT /* SearchKeywordMapper.xml, getWhiteKeywordList, SAC-전시기타/이태희/부르칸, 2015-11-30 */ RANK, KEYWORD, 0 AS RANK_VARIATION, 'DP013604' AS RANK_VARIATION_CD FROM (SELECT RANK, KEYWORD FROM TB_DP_SEARCH_WHITE_LIST WHERE TENANT_ID = #{tenantId}
AND SEARCH_ID = #{searchId}
AND EXPO_YN = 'Y' AND SYSDATE BETWEEN EXPO_START_DT AND EXPO_END_DT ORDER BY RANK ASC, KEYWORD ASC ) WHERE ROWNUM <= #{count}

SELECT /* SearchKeywordMapper.xml, getWhiteKeywordInfo, SAC-전시기타/이태희/부르칸, 2015-11-30 */ A.SEARCH_NM, A.SEARCH_DESC, A.SEARCH_CLSF_CD, (SELECT TO_CHAR(OPERATION_DT, 'YYYYMMDDHH24MISS') FROM TB_DP_SEARCH_KEYWORD WHERE TENANT_ID = A.TENANT_ID AND SEARCH_ID = A.SEARCH_ID AND STD_DT = A.STD_DT AND ROWNUM = 1) AS OPERATION_DT FROM TB_DP_SEARCH A WHERE A.TENANT_ID = #{tenantId}
AND A.SEARCH_ID = #{searchId}
AND A.SEARCH_CLSF_CD = #{searchClsfCd}
