mapper.select:
   		/* SacServiceMapper.xml, detail, SAC : 서대영, 2014-08-11 */
        SELECT *		
        FROM TB_CM_SERVICE
		WHERE service_cd = #{serviceCd}

   		/* SacServiceMapper.xml, detail, SAC : 서대영, 2014-08-11 */
        SELECT SIM_OPERATOR	
        FROM TB_CM_SERVICE_SIM_OPERATOR
		WHERE service_cd = #{serviceCd}

   		/* SacServiceMapper.xml, detail, SAC : 서대영, 2014-08-11 */
        SELECT MODEL	
        FROM TB_CM_SERVICE_MODEL
		WHERE service_cd = #{serviceCd}
		ORDER BY service_cd
